mapper.select:
SELECT * FROM SYS_SCENIC_SPOT a INNER JOIN SYS_SCENIC_SPOT_BROADCAST b ON a.SCENIC_SPOT_ID = b.SCENIC_SPOT_ID INNER JOIN SYS_SCENIC_SPOT_BROADCAST_EXTEND d ON b.BROADCAST_ID = d.BROADCAST_ID LEFT JOIN sys_scenic_district_ranking c ON b.BROADCAST_ID = c.broadcast_id
AND c.type = #{type,jdbcType=VARCHAR}
AND c.type = #{types,jdbcType=VARCHAR}
WHERE
b.SCENIC_SPOT_ID = #{scenicSpotId,jdbcType=BIGINT} AND b.BROADCAST_NAME like concat ('%',#{broadcastName},'%')
b.SCENIC_SPOT_ID = #{scenicSpotId,jdbcType=BIGINT} AND b.SORT_TYPE = 0 ORDER BY c.update_time DESC

mapper.update:
UPDATE  SYS_SCENIC_SPOT_BROADCAST
SET     HEAT = HEAT + 1
WHERE   BROADCAST_ID = #{id}
