mapper.select:
select sss.* ,sssi.SCNEIC_SPOT_IMG_URL,sssa.SCNEIC_SPOT_AUDIO_URL, sssb1.SCENIC_SPOT_FNAME as provinceName , sssb2.SCENIC_SPOT_FNAME as cityName,sssb2.SCENIC_SPOT_FID as scenicSpotSid, sssb3.SCENIC_SPOT_FNAME as areaName,sssb3.SCENIC_SPOT_FID as scenicSpotQid, (select total from sys_scenic_spot_heat where type = 1 and scenic_spot_id = sss.scenic_spot_id) as hotSearchCount , (select total from sys_scenic_spot_heat where type = 2 and scenic_spot_id = sss.scenic_spot_id) as peopleSearchCount , (select total from sys_scenic_spot_heat where type = 3 and scenic_spot_id = sss.scenic_spot_id) as welcomeSearchCount , (select total from sys_scenic_spot_heat where type = 4 and scenic_spot_id = sss.scenic_spot_id) as collectionSearchCount , (select total from sys_scenic_spot_heat where type = 5 and scenic_spot_id = sss.scenic_spot_id) as likeSearchCount, sssb1.SCENIC_SPOT_FNAME from SYS_SCENIC_SPOT sss left join SYS_SCENIC_SPOT_IMG sssi on sss.SCENIC_SPOT_ID = sssi.SCENIC_SPOT_ID left join sys_scenic_spot_heat sssh on sss.SCENIC_SPOT_ID = sssh.scenic_spot_id left join SYS_SCENIC_SPOT_BINDING sssb on sss.SCENIC_SPOT_ID = sssb.SCENIC_SPOT_FID left join SYS_SCENIC_SPOT_AUDIO sssa on sss.SCENIC_SPOT_ID = sssa.SCENIC_SPOT_ID left join SYS_SCENIC_SPOT_BINDING sssb1 on sssb.SCENIC_SPOT_PID = sssb1.SCENIC_SPOT_FID and sssb1.SCENIC_SPOT_TYPE = 1 left join SYS_SCENIC_SPOT_BINDING sssb2 on sssb.SCENIC_SPOT_SID = sssb2.SCENIC_SPOT_FID and sssb2.SCENIC_SPOT_TYPE = 3 left join SYS_SCENIC_SPOT_BINDING sssb3 on sssb.SCENIC_SPOT_QID = sssb3.SCENIC_SPOT_FID and sssb3.SCENIC_SPOT_TYPE = 4
1=1
and sss.SCENIC_SPOT_NAME like concat('%',#{spotName},'%')
and sss.SCENIC_SPOT_ID = #{spotId}
and sss.ROBOT_WAKEUP_WORDS = #{robotWakeupWords}
and sssb.SCENIC_SPOT_PID = #{spotFid}
and sssb.SCENIC_SPOT_SID = #{spotSid}
and sssb.SCENIC_SPOT_QID = #{spotQid}
GROUP BY sss.SCENIC_SPOT_ID
ORDER BY hotSearchCount DESC
ORDER BY peopleSearchCount DESC
ORDER BY welcomeSearchCount DESC
ORDER BY collectionSearchCount DESC
ORDER BY likeSearchCount DESC
ORDER BY CREATE_DATE DESC

select sss.SCENIC_SPOT_ID,sss.SCENIC_SPOT_NAME,sss.SCENIC_SPOT_CONTACT,sss.SCENIC_SPOT_PHONE,sss.SCENIC_SPOT_EMAIL, sss.SCENIC_SPOT_ADDRES,sss.SCENIC_SPOT_POSTAL_CODE,sss.CREATE_DATE,sss.ROBOT_WAKEUP_WORDS,sss.START_LEVEL,sss.SCENIC_SPOT_COMPANY, sssb.SCENIC_SPOT_FNAME as scenicSpotFname from SYS_SCENIC_SPOT sss left join SYS_SCENIC_SPOT_BINDING sssb on sss.SCENIC_SPOT_FID = sssb.SCENIC_SPOT_FID
sss.SCENIC_SPOT_NAME like concat('%',#{spotName},'%')
sss.SCENIC_SPOT_ID = #{spotId}
and sss.ROBOT_WAKEUP_WORDS = #{robotWakeupWords}
and sss.SCENIC_SPOT_FID = #{spotFid}
order by CREATE_DATE DESC

select *
from SYS_SCENIC_SPOT
where SCENIC_SPOT_ID = #{scenicSpotId}
or SCENIC_SPOT_NAME = #{scenicSpotName}
